+import { useState } from "react";
+import { useDispatch, useSelector } from "react-redux";
+import { Link, useNavigate } from "react-router-dom";
+import { userLogin } from "../redux/slices/auth";
+import Input from "../components/Input";
+import ValidationError from "../components/ValidationError";
+import { Container, Form, Button, Col, Row } from "react-bootstrap";
+import loginLogo from "../assets/login.svg";
+import "../style/login.css";
+
+
+const Login = () => {
+    const [email, setEmail] = useState("");
+    const [password, setPassword] = useState("");
+    const { isLoading } = useSelector((state) => state.auth);
+    const dispatch = useDispatch();
+    const navigate = useNavigate();
+
+    const loginHandler = async (e) => {
+        e.preventDefault();
+        dispatch(userLogin({ email, password }));
+        navigate("/");
+    };
+
+
+    return (
+        <>
+            <section>
+                <Container id="main-container" className="d-grid h-100">
+                    <Row>
+                        <Col md={6}>
+                            <img src={loginLogo} alt="Image" className="img-fluid primary" />
+                        </Col>
+                        <Col md={6} className="contents">
+                            <Row className="justify-content-center">
+                                <Col md={8}>
+                                    <div className="mb-4">
+                                        <h3>Sign In</h3>
+                                        <p className="mb-4">
+                                            Lorem ipsum dolor sit amet elit. Sapiente sit aut eos
+                                            consectetur adipisicing.
+                                        </p>
+                                    </div>
+
+                                    <Form id="sign-in-form" className="text-center p-3 w-100">
+                                        <ValidationError />
+
+                                        <Form.Group className="mb-3" controlId="formGroupEmail">                                            <Input
+                                            type={"email"}
+                                            label={"Email"}
+                                            state={email}
+                                            setState={setEmail}
+                                        />
+                                        </Form.Group>
+                                        <Form.Group className="mb-3" controlId="formGroupPassword">
+                                            <Input
+                                                type={"password"}
+                                                label={"Password"}
+                                                state={password}
+                                                setState={setPassword}
+
+                                            />
+                                        </Form.Group>
+
+                                        <Button
+                                            variant="primary"
+                                            size="lg"
+                                            className="w-100 mt-2"
+                                            type="submit"
+                                            disabled={isLoading}
+                                            onClick={loginHandler}
+                                        >
+                                            {isLoading ? "Loading..." : "Login"}
+                                        </Button>
+                                        <div className="d-flex mb-5 align-items-center">
+
+
+                                            {/* <span className="ml-auto">
+                                                <Link href="#" className="forgot-pass text-center">
+                                                    Forgot Password
+                                                </Link>
+                                            </span> */}
+                                        </div>
+                                    </Form>
+                                </Col>
+                            </Row>
+                        </Col>
+                    </Row>
+                </Container>
+            </section>
+        </>
+    );
+};
+
+export default Login;

