import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

import { userRegister } from "../redux/slices/auth";
import Input from "../components/Input";
import ValidationError from "../components/ValidationError";
import { Container } from "react-bootstrap";

const Register = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { isLoading } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const registerHandler = async (e) => {
    e.preventDefault();
    dispatch(
      userRegister({ username: username, email: email, password: password })
    );
    navigate("/");
  };

  return (
    <section>
      <Container className="text-center mt-5">
        <div className="form-signin w-100 m-auto">
          <form>
            <img
              src={"/"}
              alt="icon image"
              style={{ width: "75px", height: "74px" }}
            />
            <h1 className="h3 my-3 fw-normal">Register</h1>
            <ValidationError />
            <Input label={"Username"} state={username} setState={setUsername} />
            <Input
              type={"email"}
              label={"Email"}
              state={email}
              setState={setEmail}
            />
            <Input
              type={"password"}
              label={"Password"}
              state={password}
              setState={setPassword}
            />

            <button
              className="w-100 btn btn-lg btn-primary mt-2"
              type="submit"
              disabled={isLoading}
              onClick={registerHandler}
            >
              {isLoading ? "Loading..." : "Register"}
            </button>
          </form>
        </div>
      </Container>

      <Container id="main-container" className="d-grid h-100">
        <Row>
          <Col md={6}>
            <img src={loginLogo} alt="Image" className="img-fluid primary" />
          </Col>
          <Col md={6} className="contents">
            <Row className="justify-content-center">
              <Col md={8}>
                <div className="mb-4">
                  <h3>Sign In</h3>
                  <p className="mb-4">
                    Lorem ipsum dolor sit amet elit. Sapiente sit aut eos
                    consectetur adipisicing.
                  </p>
                </div>

                <Form id="sign-in-form" className="text-center p-3 w-100">
                  <ValidationError />

                  <Form.Group className="mb-3" controlId="formGroupEmail">                                            <Input
                    type={"email"}
                    label={"Email"}
                    state={email}
                    setState={setEmail}
                  />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formGroupPassword">
                    <Input
                      type={"password"}
                      label={"Password"}
                      state={password}
                      setState={setPassword}

                    />
                  </Form.Group>

                  <Button
                    variant="primary"
                    size="lg"
                    className="w-100 mt-2"
                    type="submit"
                    disabled={isLoading}
                    onClick={loginHandler}
                  >
                    {isLoading ? "Loading..." : "Login"}
                  </Button>
                  <div className="d-flex mb-5 align-items-center">
                    <span className="ml-auto mt-2">
                      <p className="forgot-pass text-dark">Don't have any account ? <Link to={'/register'}>Signup</Link > </p>
                    </span>
                  </div>
                </Form>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Register;
